static inline void init_dc()
{
    // enter DC data input mode
    P1OUT |= VPRG;

    // set everything to 0xFF
    P2OUT |= SIN1;
    P2OUT |= SIN2;
    P2OUT |= SIN3;
    P2OUT |= SIN4;
    P2OUT |= SIN5;
    P2OUT |= SIN6;

    // DC register is 96 bits, we have 6 chips
    for (i=0; i<(96); i++)
    {
        P2OUT |= SCLK;
        P2OUT &= ~SCLK;
    }

    P2OUT &= ~SIN1;
    P2OUT &= ~SIN2;
    P2OUT &= ~SIN3;
    P2OUT &= ~SIN4;
    P2OUT &= ~SIN5;
    P2OUT &= ~SIN6;

    // latch in the data
    P2OUT |= XLAT;
    P2OUT &= ~XLAT;
    P2OUT |= SCLK;
    P2OUT &= ~SCLK;
    // switch back to GS mode
    P1OUT &= ~VPRG;

    need_extra_sclk = 1;
}

